require("my.awful.dbus")local load=loadstring or load;local tostring=tostring;local ipairs=ipairs;local table=table;local unpack=unpack or table.unpack;local dbus=dbus;local type=type;if dbus then dbus.connect_signal("org.awesomewm.awful.Remote",function(a,b)if a.member=="Eval"then local c,d=load(b)if not c then return"s",d end;local e={pcall(c)}if not table.remove(e,1)then return"s","Error during execution: "..tostring(e[1])end;local f={}for g,h in ipairs(e)do local i=type(h)if i=="boolean"then table.insert(f,"b")table.insert(f,h)elseif i=="number"then table.insert(f,"d")table.insert(f,h)else table.insert(f,"s")table.insert(f,tostring(h))end end;return unpack(f)end end)end